INSERT INTO `scence_des` VALUES (159, '12-00-1', '-', '符合', '通过部署国密视频监控系统，视频存储设备获得密码产品认证，通过该设备安全存储物理环境视频音像监控记录数据，防止记录被非授权篡改。\r\n{通过部署密码设备，采用密码技术对物理环境视频音像监控记录数据进行完整性保护，防止记录被非授权篡改。（接外部密码设备）}\r\n通过部署国密视频监控系统，视频存储设备获得密码产品认证，通过该设备安全存储物理环境视频音像监控记录数据，防止记录被非授权篡改。\r\n{通过部署密码设备，采用密码技术对物理环境视频音像监控记录数据进行完整性保护，防止记录被非授权篡改。（接外部密码设备）}\r\n', '-', '-');
