INSERT INTO `scence_des` VALUES (163, '10-02-1', '普通电子门禁系统', '不符合', '机房门口部署了非国密电子门禁系统，未采用密码技术进行进入人员的身份鉴别，存在非法人员进入系统所在物理机房等重要物理环境，对软硬件设备和数据进行直接破坏的风险。', '-', '-');
