INSERT INTO `scence_des` VALUES (169, '50-01-1', '国密电子门禁系统', '符合', '物理和环境安全中的身份鉴别属于高风险项。但考虑到系统门禁系统现状和资金问题，可使用缓解措施：（1）机房部署电子门禁系统，通过指纹/人脸方式对进出机房的人员进行身份鉴别；（2）在机房出入口配备专人值守并进行登记，且采用视频监控系统进行实时监控，{视频监控系统由专人进行值守，定期检查门禁和视频监控系统的安全状态。}。采用上述方式，属于此高风险项的缓解措施，可酌情降低风险等级。', '符合', '符合');
INSERT INTO `scence_des` VALUES (170, '51-01-1', '国密电子门禁系统', '符合', '物理和环境安全中的电子门禁系统进出数据记录的存储完整性非高风险项。考虑到系统门禁系统现状和资金问题，可不使用国密电子门禁系统，不做改造。该项会达不到要求而不得分，但整体分值及格仍可通过密评。', '符合', '符合');
INSERT INTO `scence_des` VALUES (171, '52-01-1', '国密电子监控系统', '符合', '物理和环境安全中的电子监控系统进出电子监控音像记录数据的存储完整性非高风险项。考虑到电子监控系统现状和资金问题，可不使用国密电子监控系统，不做改造。该项会达不到要求而不得分，但整体分值及格仍可通过密评。', '符合', '符合');
INSERT INTO `scence_des` VALUES (172, '50-02-1', '-', '不符合', '采用基于密码技术的遵循GM/T 0036要求的电子门禁系统，保护物理访问控制身份鉴别信息。符合该标准的电子门禁系统使用SM4等算法进行密钥分散，实现门禁卡的一卡一密，并基于SM4等算法鉴别人员身份。', '不符合', '不符合');
INSERT INTO `scence_des` VALUES (173, '51-02-1', '-', '不符合', '采用基于密码技术的遵循GM/T 0036要求的电子门禁系统，电子门禁系统进出记录应严格进行完整性保护，完整的进出记录是实现安全管理、事后追责的重要基础。符合该标准的电子门禁系统使用采用MAC或数字签名等技术对记录进行完整性保护，保证进出数据记录的存储完整性。', '不符合', '不符合');
INSERT INTO `scence_des` VALUES (174, '52-02-1', '-', '不符合', '采用基于密码技术的视频监控系统，视频监控系统的音像记录应严格进行完整性保护，完整的音像记录是实现安全管理、事后追责的重要基础。使用采用MAC或数字签名等技术对记录进行完整性保护，保证视频监控音像记录数据的存储完整性。', '不符合', '不符合');
INSERT INTO `scence_des` VALUES (175, '70-00-1', 'USBKey、国密数字证书、签名验签服务器', '符合', '在密码基础设施区部署堡垒机，运维人员PC端配发已通过商用密码产品检测认证的USBKey（内有合规CA签发的国密SM2证书），配合已通过商用密码产品检测认证的签名验签服务器，在登录时验证运维人员USBKey中的国密数字证书，实现运维人员的身份鉴别。', '符合', '符合');
