INSERT INTO `scence_des` VALUES (210, '80-00-1', '智能密码钥匙、国密安全密码应用中间件、签名验签服务器', '符合', '部署符合GB/T 38629-2020《信息安全技术 签名验签服务器技术规范》标准要求的签名验签服务器，系统管理员、普通用户通过PC客户端登录业务系统时，使用用户名+口令配合智能密码钥匙登录系统，智能密码钥匙中存有具有电子政务电子认证服务许可机构颁发的SM2、SM3算法数字证书，并在密码基础设施区部署签名验签服务器，用户登录时验证数字签名，防止非授权人员登录。', '符合', '符合');
INSERT INTO `scence_des` VALUES (211, '80-01-1', '智能密码钥匙、可信浏览器、签名验签服务器', '符合', '部署遵循GM/T 0024-2014《SSL VPN网关产品规范》标准要求的SSL VPN安全网关，系统管理员和普通用户通过可信浏览器登录系统时，可信浏览器提前适配SSL VPN安全网关，将智能密码钥匙中的证书发送到签名验签服务器。智能密码钥匙中的个人证书是具有电子政务电子认证服务许可机构颁发的SM2SM3算法数字证书，服务端签名验签服务器对证书进行校验，并结合国密SSL登录，防止非授权人员登录。', '符合', '符合');
INSERT INTO `scence_des` VALUES (212, '80-02-1', '协同签名系统', '符合', '用户使用移动端登录系统时，协同签名系统手机端与服务器端分别根据自身密钥因子计算中间签名结果，最后合成符合规范的电子签名，并可以利用对应的公钥进行验签，实现移动端登录用户的身份验证，防止非授权人员登录。', '符合', '符合');
INSERT INTO `scence_des` VALUES (213, '80-03-1', '-', '不符合', '公众用户通过用户名+口令配合短信验证码登录，实现登录应用用户的身份鉴别，防止非授权人员登录。', '不符合', '不符合');
INSERT INTO `scence_des` VALUES (214, '81-00-1', '国密安全密码应用中间件、服务器密码机', '符合', '在密码基础设施区部署符合GM/T 0030-2014《服务器密码机技术规范》标准要求的服务器密码机和具有商密资质的国密安全密码应用中间件，业务系统通过国密安全密码应用中间件调用服务器密码机使用HMAC-SM3算法对系统应用的用户角色分配表、系统权限分配表、数据库权限分配表等访问控制信息进行完整性保护，防止访问控制信息被篡改。', '符合', '符合');
