INSERT INTO `scence_des` VALUES (234, '60-00-1', 'IPSec VPN安全网关', '符合', '部署符合GM/T 0022-2014《IPSec VPN 技术规范》、GM/T 0023-2014《IPSec VPN 网关产品规范》的IPSec VPN安全网关，支持国密算法，已通过商用密码机构认证，并配置由具有电子政务电子认证服务许可机构采用SM2、SM3算法颁发的数字证书，IPSec VPN安全网关通过基于SM2算法的国密数字证书对通信实体进行身份鉴别，需对服务端的证书进行身份鉴别，保证通信过程中通信实体身份鉴别的真实性。', '符合', '符合');
